mapper.select:
SELECT * FROM USERS
WHERE PROVIDER = #{provider}
  AND PROVIDER_UID = #{providerUid}

mapper.insert:
INSERT INTO USERS (
    USER_ID, NAME, EMAIL, PROFILE_URL, PROVIDER, PROVIDER_UID
) VALUES (
             #{userId, jdbcType=VARCHAR}, #{name}, #{email}, #{profileUrl}, #{provider}, #{providerUid}
         )
